    function initScroll() {
        if (scrollContainer) {
            const handleScroll = () => {
                gsap.to(scrollContainer, {
                    x: -window.scrollY * 0.5,
                    ease: "power2.out",
                    duration: 0.3,
                });
            };

            window.addEventListener("scroll", handleScroll);
            return () => window.removeEventListener("scroll", handleScroll);
        }
    }

    onMount(() => {
        return initScroll();
    });

    $: if ($isQuizMode && scrollContainer) {
        initScroll();
    }
</script>

{#if $isQuizMode}
<section class="overflow-hidden">
    <div bind:this={scrollContainer} class="scroll-container flex gap-5 pb-40 pt-20">
        {#each circles as circle}
            <div class="circle-item flex items-center gap-5">
                <div class="circle w-40 h-40 bg-lightblue rounded-full flex items-center justify-center">
                    {#if circle.icon}
                        {@html circle.icon}
                    {/if}
                </div>
                <h2 class="text-2xl font-semibold">{circle.title}</h2>
            </div>
        {/each}
    </div>
</section>
{/if}


<style>
    /* Styles for each circular element */
    .circle-item {
        display: flex; /* Aligns circle and text horizontally */
        align-items: center; /* Centers circle and text vertically */
        transition: transform 0.3s ease-in-out;
        white-space: nowrap;
    }

    /* Responsive sizes for circles */
    @media (min-width: 640px) {
        .circle-item .circle {
            width: 50px;
            height: 50px;
        }
    }

    @media (min-width: 1024px) {
        .circle-item .circle {
            width: 50px;
            height: 50px;
        }
    }
</style>
